import React, { useEffect, useState } from "react";
import "./Profile.css";
import axios from "axios";
import Topbar from "../../components/topbar/Topbar";
import Sidebar from "../../components/sidebar/Sidebartwo";
import Rightbar from "../../components/rightbar/Rightbar";
import Timeline from "../../components/timeline/Timeline";
import { useParams } from "react-router-dom";

export default function Profile() {
  const PUBLIC_FOLDER = process.env.REACT_APP_PUBLIC_FOLDER;

  const [user, setUser] = useState({});
  const [followers, setFollowers] = useState({});
  const [file, setFile] = useState(null);

  const username = useParams().username;

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await axios.get(`/api/users?username=${username}`);
        console.log(response.data);
        setUser(response.data);
      } catch (error) {
        console.error(
          "Error fetching user:",
          error.response ? error.response.data : error.message
        );
      }
    };
    fetchUser();
  }, [username]);

  useEffect(() => {
    const fetchFollowers = async () => {
      try {
        const response = await axios.get(`/api/users/${user._id}/followers`);
        setFollowers(response.data);
      } catch (err) {
        console.error("Error fetching followers:", err);
      }
    };
    if (user._id) {
      fetchFollowers();
    }
  }, [user._id]);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (file) {
      const data = new FormData();
      data.append("file", file);
      data.append("userId", user._id);

      try {
        const res = await axios.post("/api/upload/cover", data);
        const updatedUser = { ...user, coverPicture: res.data.filename };
        setUser(updatedUser);
      } catch (err) {
        console.error("Error uploading file:", err);
      }
    }
  };

  return (
    <>
      <Topbar />

      <div className="profile">
        <Sidebar />
        <div className="profileRight">
          <section className="profileCover">
            <h1 className="hello">Hi</h1>
            <img
              src={
                user.coverPicture
                  ? PUBLIC_FOLDER + "/images/" + user.coverPicture
                  : PUBLIC_FOLDER + "/post/3.jpeg"
              }
              alt=""
              className="profileCoverImg"
            />
            <input type="file" onChange={handleFileChange} />
            <button onClick={handleUpload}>Upload</button>
          </section>
          <section className="below">
            <div className="profileInfo">
              <h4 className="profileInfoName">{user.username}</h4>
              <span className="profileInfoDesc">{user.desc}</span>
            </div>
          </section>
          <div className="profileRightBottom">
            <Timeline username={username} gridView={true} pageType="profile" />
          </div>
          {/* <h2 className="general">general posts</h2>
          <Timeline
            username={username}
            gridView={true}
            showShare={false}
            pageType="profile"
          /> */}
        </div>
      </div>
    </>
  );
}
