import React, { useContext, useEffect, useState } from "react";
import "./Timeline.css";
import Share from "../share/Share";
import Post from "../post/Post";
import axios from "axios";
import { AuthContext } from "../../context/AuthContext";

export default function Timeline({
  username,
  gridView = false,
  showShare = true,
}) {
  const [posts, setPosts] = useState([]);
  const { user } = useContext(AuthContext);

  // useEffect(() => {
  //   const fetchPosts = async () => {
  //     try {
  //       const response = username
  //         ? await axios.get(`/posts/profile/${username}`) // profile の場合
  //         : await axios.get(`/posts/timeline/${user._id}`); // home
  //       setPosts(response.data); // 取得したデータを state に保存
  //     } catch (error) {
  //       console.error(
  //         "Error fetching posts:",
  //         error.response ? error.response.data : error.message
  //       );
  //     }
  //   };
  //   fetchPosts();
  // }, [username, user._id]);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = username
          ? await axios.get(`/posts/profile/${username}`) // profile の場合
          : await axios.get(`/posts/timeline/${user._id}`); // home の場合

        setPosts(response.data);
      } catch (error) {
        console.error(
          "Error fetching posts:",
          error.response ? error.response.data : error.message
        );
      }
    };

    if (username || (user && user._id)) {
      // user オブジェクトが存在するかチェック
      fetchPosts();
    }
  }, [username, user]);

  return (
    <div className="timeline">
      <div className={`timelineWrapper ${gridView ? "gridView" : ""}`}>
        {showShare && <Share />}
        {posts.map((post) => (
          <div className="postWrapper" key={post._id}>
            <Post post={post} />
          </div>
        ))}
      </div>
    </div>
  );
}
