import React, { useContext, useEffect, useState } from "react";
import "./Timeline.css";
import Share from "../share/Share";
import Post from "../post/Post";
import axios from "axios";
import { AuthContext } from "../../context/AuthContext";

export default function Timeline({ username, gridView = false }) {
  const [posts, setPosts] = useState([]);
  const { user } = useContext(AuthContext);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = username
          ? await axios.get(`/posts/profile/${username}`) // profile の場合
          : await axios.get(`/posts/timeline/${user._id}`); // home
        console.log(response.data); // デバッグのために取得したデータをログに出力
        setPosts(response.data); // 取得したデータを state に保存
      } catch (error) {
        // エラーハンドリングの強化
        console.error(
          "Error fetching posts:",
          error.response ? error.response.data : error.message
        );
      }
    };
    fetchPosts();
  }, [username, user._id]); // usernameが変更されるたびにデータを再取得

  return (
    <div className="timeline">
      <div className={`timelineWrapper ${gridView ? "gridView" : ""}`}>
        <Share />
        {posts.map((post) => (
          <div className="postWrapper" key={post._id}>
            <Post post={post} />
          </div>
        ))}
      </div>
    </div>
  );
}
